import React from 'react';
import { HighlightSpan } from '../types';
import { getLevelClassName } from '../utils';
import Tooltip from './Tooltip';

interface SuggestionOverlayProps {
  spans: HighlightSpan[];
}

const SuggestionOverlay: React.FC<SuggestionOverlayProps> = ({ spans }) => {
  if (spans.length === 0) {
    return null;
  }

  return (
    <div className="w-full p-4 border border-gray-300 rounded-lg bg-white font-mono text-sm leading-relaxed whitespace-pre-wrap">
      {spans.map((span, index) => {
        if (span.isHighlighted && span.suggestion) {
          return (
            <Tooltip key={index} suggestion={span.suggestion}>
              <span className={getLevelClassName(span.suggestion.level)}>
                {span.text}
              </span>
            </Tooltip>
          );
        }
        return <span key={index}>{span.text}</span>;
      })}
    </div>
  );
};

export default SuggestionOverlay;
