import { OpenAIResponse } from './types';

const OPENAI_API_URL = 'https://api.openai.com/v1/chat/completions';

const SYSTEM_PROMPT = `You are a helpful writing assistant. Review the text and return a list of editing suggestions in the following format: JSON with an array of objects, each containing:
- 'level' (1, 2, or 3),
- 'start' and 'end' character indices in the original string,
- 'original' (substring),
- 'replacement' (optional),
- 'reason' (brief explanation).
Use level 1 for grammar, 2 for cohesion/structure, 3 for content suggestions. Return only the JSON.`;

export const analyzeText = async (text: string, apiKey: string): Promise<OpenAIResponse> => {
  if (!apiKey) {
    throw new Error('OpenAI API key is required');
  }

  try {
    const response = await fetch(OPENAI_API_URL, {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json',
        'Authorization': `Bearer ${apiKey}`,
      },
      body: JSON.stringify({
        model: 'gpt-4o',
        messages: [
          {
            role: 'system',
            content: SYSTEM_PROMPT,
          },
          {
            role: 'user',
            content: text,
          },
        ],
        temperature: 0.3,
        response_format: { type: 'json_object' },
        max_tokens: 2000,
      }),
    });

    if (!response.ok) {
      const errorData = await response.json();
      throw new Error(`OpenAI API error: ${errorData.error?.message || 'Unknown error'}`);
    }

    const data = await response.json();
    const content = data.choices[0]?.message?.content;

    if (!content) {
      throw new Error('No response content from OpenAI');
    }

    // Content can be already an object when using JSON mode
    if (typeof content === 'object') {
      return content as OpenAIResponse;
    }

    // Fallback: try to parse string content
    try {
      const parsed = JSON.parse(content);
      return parsed;
    } catch (parseError) {
      console.error('Failed to parse OpenAI response:', content);
      throw new Error('Invalid JSON response from OpenAI');
    }
  } catch (error) {
    console.error('Error calling OpenAI API:', error);
    throw error;
  }
};
