export interface Suggestion {
  level: 1 | 2 | 3;
  start: number;
  end: number;
  original: string;
  replacement?: string;
  reason: string;
}

export interface OpenAIResponse {
  suggestions: Suggestion[];
}

export interface HighlightSpan {
  text: string;
  suggestion?: Suggestion;
  isHighlighted: boolean;
}
